         }
         else if (keyCode === 37 && rot) {
           if (curPos > 0){
             curPos -= 1
           } else {
             curPos = targetPoints.length - 1;
           }
           camera.lookAt(targetPoints[curPos][0] , targetPoints[curPos][1], targetPoints[curPos][2]);
